import React, { useState } from 'react'
import axios from 'axios'

const Form = () => {
    const [title, setTitle] = useState("");
    const [price, setPrice] = useState("");
    const [description, setDescription] = useState("");

    const submitHandler = e => {
        e.preventDefault();
        axios.post("http://localhost:8000/api/products", {
            title,
            price,
            description
        })
            .then(res => console.log(res))
            .catch(err => console.log(err))
    }

    return (
        <div>
            <form onSubmit={ submitHandler }>
                <label htmlFor="title">Title: </label><br/>
                <input type="text" name="title" onChange={(e) => setTitle(e.target.value)}/><br/>
                <label htmlFor="price">Price: </label><br/>
                <input type="number" name="price" onChange={(e) => setPrice(e.target.value)}/><br/>
                <label htmlFor="description">Description: </label><br/>
                <input type="text" name="description" onChange={(e) => setDescription(e.target.value)}/><br/>
                <input type="submit" value="Create"/>
            </form>
        </div>
    )
}

export default Form
