import {useState} from 'react'
import axios from 'axios';
import Form from './Form';
import { navigate } from '@reach/router';

const CreateProduct = () => {
    const [product, setProduct] = useState({
        title: "",
        price: "",
        description: ""
    });

    const [error, setError] = useState({
        title: "",
        price: "",
        description: ""
    })

    const submitData = () => {
        axios.post("http://localhost:8000/api/products", product)
            .then(response => {
                navigate("/")
                if(response.data.message == "success"){
                    navigate("/");
                }
                else {
                    console.log(response.data.results);
                    if (response.data.results.errors.title){
                        setError({
                            ...error,
                            name: response.data.results.error.title.properties.message
                        })
                    }
                    if (response.data.results.errors.price){
                        setError({
                            ...error,
                            name: response.data.results.error.price.properties.message
                        })
                    }
                    if (response.data.results.errors.description){
                        setError({
                            ...error,
                            name: response.data.results.error.description.properties.message
                        })
                    }
                }
            })
            .catch(err => {
                console.log(err);
            })
    }




    return (
        <div>
            <Form data={ product } setData={setProduct} submitData={submitData}/>
        </div>
    )
}

export default CreateProduct
